  //let randomSimpleRanged = simpleRanged[Math.floor(Math.random() * 5)];
  //let randomMartialRanged = martialRanged[Math.floor(Math.random() * 6)];
};

//will randomize equipment based off class; preferential logic will usually outfit a character with better equipment
//if class has multiple proficiencies, eg both simple and martial weapons,
//a roll for one will be made before the roll for the individual equipment is finished
const getEquipment = (target) => {
  let temp = [];
  let rngWep = null;
  let rngArmor = null;
  switch (target) {
    case "artificer":
      rngWep = Math.floor(Math.random() * 11);
      if (rngWep >= 4) {
        temp.push(weaponSmith("simpleM"));
      } else {
        temp.push(weaponSmith("simpleR"));
      }

      rngArmor = Math.floor(Math.random() * 11);
      if (rngArmor >= 7) {
        temp.push(armorSmith("med"));
      } else {
        temp.push(armorSmith("light"));
      }
      break;
    case "barbarian":
      rngWep = Math.floor(Math.random() * 11);
      if (rngWep > 5) {
        temp.push(weaponSmith("martialM"));
      } else if (rngWep <= 5 && rngWep > 2) {
        temp.push(weaponSmith("simpleM"));
      } else if (rngWep <= 2 && rngWep > 0) {
        temp.push(weaponSmith("martialR"));
      } else {
        temp.push(weaponSmith("simpleR"));
      }

      rngArmor = Math.floor(Math.random() * 11);
      if (rngArmor >= 7) {
        temp.push(armorSmith("med"));
      } else {
        temp.push(armorSmith("light"));
      }
      break;
    case "bard":
      break;
    case "cleric":
      break;
    case "druid":
      break;
    case "fighter":
      break;
    case "monk":
      break;
    case "paladin":
      break;
    case "ranger":
      break;
    case "rogue":
      break;
    case "sorcerer":
      break;
    case "warlock":
      break;
    case "wizard":
      break;
    default:
      return;
  }

  return temp;
};

export default getEquipment;
