import React, { useState } from "react";
import "./input-block.css";

//components
import CheckboxPair from "./checkbox-pair";

export default function InputBlock(props) {
  //states
  const [inputVal, setInputVal] = useState(null);
  const [classType, setClassType] = useState(null);
  const [checkboxArray, setCheckboxArray] = useState([
    { name: "artificer", state: false }, //0
    { name: "barbarian", state: false }, //1
    { name: "bard", state: false }, //2
    { name: "cleric", state: false }, //3
    { name: "druid", state: false }, //4
    { name: "fighter", state: false }, //5
    { name: "monk", state: false }, //6
    { name: "paladin", state: false }, //7
    { name: "ranger", state: false }, //8
    { name: "rogue", state: false }, //9
    { name: "sorcerer", state: false }, //10
    { name: "warlock", state: false }, //11
    { name: "wizard", state: false }, //12
  ]);

  //packs input and predetermined choice(if any) into state held in parent component: displayArray
  const submitHandler = () => {
    // props.displayArray
    // props.setDisplayArray()
    console.log("gets to submit");
    let newField = { name: inputVal, class: classType };
    props.setDisplayArray((prevState) => {
      let newState = [...props.displayArray, newField];
      return newState;
    });
  };

  const inputChangeHandler = (event) => {
    setInputVal(event.target.value);
  };

  const checkBoxHandler = (event) => {
    let temp = event.target.name;
    switch (temp) {
      case "artificer":
        console.log("makes it");
        break;
      case "not":
        console.log("doesnt make it");
        break;
      default:
      // code block
    }
  };

  return (
    <div className="input-block-container">
      <form
        onSubmit={(event) => {
          event.preventDefault();
          submitHandler();
        }}
      >
        {/* requires name */}
        <label>Name: </label>
        <input
          type="text"
          onChange={(event) => {
            inputChangeHandler(event);
          }}
          value={inputVal}
          required
        ></input>
        {/* can choose predetermined class */}
        <div className="class-container">
          <ul>
            <CheckboxPair checkBoxHandler={checkBoxHandler} />;
          </ul>
        </div>
        <button type="submit">Generate</button>
      </form>
    </div>
  );
}
