import React, { useEffect, useState } from "react";

export default function DisplayBlock(props) {
  console.log(props.displayArray);
  const [weaponsCho, setWeaponsCho] = useState("");
  const [armorCho, setArmorCho] = useState("");

  //all in game stock equipment
  //melee
  const simpleMelee = [
    "club",
    "dagger",
    "greatclub",
    "handaxe",
    "javelin",
    "light hammer",
    "mace",
    "quarterstaff",
    "sickle",
    "spear",
  ];
  const martialMelee = [
    "battleaxe",
    "flail",
    "glaive",
    "greataxe",
    "greatsword",
    "halberd",
    "lance",
    "longsword",
    "maul",
    "morningstar",
    "pike",
    "rapier",
    "scimitar",
    "shortsword",
    "trident",
    "war pick",
    "warhammer",
    "whip",
  ];

  //ranged
  const simpleRanged = ["light crossbow", "dart", "shortbow", "sling"];
  const martialRanged = [
    "blowgun",
    "hand crossbow",
    "heavy crossbow",
    "longbow",
    "net",
  ];

  //will randomize armor based off class
  const getArmor = () => {
    console.log("gets to arm");
    let randomSimple = simpleMelee[Math.floor(Math.random() * 11)];
    console.log(randomSimple);
  };
  //will randomize weapons based off class
  const getWeapons = () => {
    console.log("gets to wep");
  };

  //should run for every new displayable
  useEffect(() => {
    getArmor();
    getWeapons();
  }, [props.displayArray]);

  return (
    <div className="display-container">
      {props.displayArray.map((npc, index) => {
        return (
          <div className="npc-row" key={index}>
            <div>{npc.name}</div>
            <div>{npc.class}</div>
            <div>{weaponsCho}</div>
            <div>{armorCho}</div>
          </div>
        );
      })}
    </div>
  );
}
