import React, { useState } from "react";
import "./input-block.css";

//components
import RadioPair from "./radio-pair";

export default function InputBlock(props) {
  //states
  const [inputVal, setInputVal] = useState("");
  const [classType, setClassType] = useState(null);
  const [equipment, setEquipment] = useState([]);

  //make fetch call happen
  /* 
  const RNGApiKey = "0186f568d86e42ee940a45e2458be310";
  const requestURL =
    "https://randommer.io/api/Name?nameType=fullname&quantity=1";

  const generateName = () => {
    fetch("", { headers: {} });
  };
  */

  //classes
  const classNameArray = [
    "artificer", //0
    "barbarian", //1
    "bard", //2
    "cleric", //3
    "druid", //4
    "fighter", //5
    "monk", //6
    "paladin", //7
    "ranger", //8
    "rogue", //9
    "sorcerer", //10
    "warlock", //11
    "wizard", //12
  ];

  const armorSmith = () => {
    console.log("armor");
  };

  const weaponSmith = (type) => {
    //melee
    const simpleMelee = [
      "club",
      "dagger",
      "greatclub",
      "handaxe",
      "javelin",
      "light hammer",
      "mace",
      "quarterstaff",
      "sickle",
      "spear",
    ];
    const martialMelee = [
      "battleaxe",
      "flail",
      "glaive",
      "greataxe",
      "greatsword",
      "halberd",
      "lance",
      "longsword",
      "maul",
      "morningstar",
      "pike",
      "rapier",
      "scimitar",
      "shortsword",
      "trident",
      "war pick",
      "warhammer",
      "whip",
    ];
    //ranged
    const simpleRanged = ["light crossbow", "dart", "shortbow", "sling"];
    const martialRanged = [
      "blowgun",
      "hand crossbow",
      "heavy crossbow",
      "longbow",
      "net",
    ];

    if (type === "simpleM") {
    }

    //let randomSimpleMelee = simpleMelee[Math.floor(Math.random() * 11)];
    //let randomMartialMelee = martialMelee[Math.floor(Math.random() * 19)];
    //let randomSimpleRanged = simpleRanged[Math.floor(Math.random() * 5)];
    //let randomMartialRanged = martialRanged[Math.floor(Math.random() * 6)];
  };

  const getsShield = (npcClass) => {
    //filters out classes without shield proficiency
    if (
      npcClass === "bard" ||
      npcClass === "monk" ||
      npcClass === "rogue" ||
      npcClass === "sorcerer" ||
      npcClass === "warlock" ||
      npcClass === "wizard"
    ) {
      return false;
    } else {
      let temp = Math.floor(Math.random() * 2);
      if (temp === 0) {
        return true;
      } else {
        return false;
      }
    }
  };

  //will randomize equipment based off class; preferential logic will usually outfit a character with better equipment
  //if class has multiple proficiencies, eg both simple and martial weapons,
  //a roll for one will be made before the roll for the individual equipment is finished
  const getEquipment = (target) => {
    let temp = [];
    let rngWep = null;
    let rngArmor = null;
    switch (target) {
      case "artificer":
        rngWep = Math.floor(Math.random() * 11);
        if (rngWep >= 4) {
          temp.push(weaponSmith("simpleM"));
        } else {
          temp.push(weaponSmith("simpleR"));
        }

        rngArmor = Math.floor(Math.random() * 11);
        if (rngArmor >= 7) {
          temp.push(armorSmith("med"));
        } else {
          temp.push(armorSmith("light"));
        }
        break;
      case "barbarian":
        rngWep = Math.floor(Math.random() * 4);
        if (rngWep === 0) {
          temp.push(weaponSmith("simpleM"));
        } else if(rngWep === 1) {
          temp.push(weaponSmith("martialM"));
        }

        rngArmor = Math.floor(Math.random() * 2);
        if (rngArmor === 0) {
          temp.push(armorSmith("light"));
        } else {
          temp.push(armorSmith("med"));
        }
        break;
      case "bard":
        break;
      case "cleric":
        break;
      case "druid":
        break;
      case "fighter":
        break;
      case "monk":
        break;
      case "paladin":
        break;
      case "ranger":
        break;
      case "rogue":
        break;
      case "sorcerer":
        break;
      case "warlock":
        break;
      case "wizard":
        break;
      default:
        return;
    }

    return temp;
  };

  //updates 'inputVal' state val
  const inputChangeHandler = (event) => {
    setInputVal(event.target.value);
  };

  //updates predetermined class value if chosen
  const radioHandler = (target) => {
    console.log(target);
    switch (target) {
      case "artificer":
        setClassType("artificer");
        break;
      case "barbarian":
        setClassType("barbarian");
        break;
      case "bard":
        setClassType("bard");
        break;
      case "cleric":
        setClassType("cleric");
        break;
      case "druid":
        setClassType("druid");
        break;
      case "fighter":
        setClassType("fighter");
        break;
      case "monk":
        setClassType("monk");
        break;
      case "paladin":
        setClassType("paladin");
        break;
      case "ranger":
        setClassType("ranger");
        break;
      case "rogue":
        setClassType("rogue");
        break;
      case "sorcerer":
        setClassType("sorcerer");
        break;
      case "warlock":
        setClassType("warlock");
        break;
      case "wizard":
        setClassType("wizard");
        break;
      case "random":
        setClassType("random");
        break;
      default:
        return;
    }
  };

  //packs input and predetermined choice(if any) into state held in parent component: displayArray
  const submitHandler = () => {
    let newField = {
      name: inputVal,
      class: classType,
    };

    //handles no radio selection / random option
    if (newField.class === null || newField.class === "random") {
      newField.class =
        classNameArray[Math.floor(Math.random() * classNameArray.length)];
    }

    //vets by class
    newField.shield = getsShield(newField.class);
    newField.equipment = getEquipment(newField.class);

    //pushes into main array
    props.setDisplayArray((prevState) => {
      let newState = [...prevState, newField];
      return newState;
    });

    //resets after submit
    setInputVal("");
    setClassType(null);
  };

  return (
    <div className="input-block-container">
      <form
        onSubmit={(event) => {
          event.preventDefault();
          submitHandler();
        }}
      >
        {/* if no name, name generator API will make one */}
        <div className="form-top">
          <label>Name: </label>
          <input
            type="text"
            onChange={(event) => {
              inputChangeHandler(event);
            }}
            value={inputVal}
          ></input>
          <button type="submit">Generate</button>
        </div>

        {/* can choose predetermined class */}
        <div className="class-container">
          {/* holds: None, Artificer, Barbarian, Bard, Cleric */}
          <ul className="checkbox-grouping">
            <RadioPair name={"random"} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[0]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[1]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[2]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[3]} radioHandler={radioHandler} />
          </ul>
          {/* holds: Druid, Fighter, Monk, Paladin, Ranger */}
          <ul className="checkbox-grouping">
            <RadioPair name={classNameArray[4]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[5]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[6]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[7]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[8]} radioHandler={radioHandler} />
          </ul>
          {/* Rogue, Sorcerer, Warlock, Wizard */}
          <ul className="checkbox-grouping">
            <RadioPair name={classNameArray[9]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[10]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[11]} radioHandler={radioHandler} />
            <RadioPair name={classNameArray[12]} radioHandler={radioHandler} />
          </ul>
        </div>
      </form>
    </div>
  );
}
