import React, { useState } from "react";
import "./input-block.css";

//components
import RadioPair from "./radio-pair";

export default function InputBlock(props) {
  //states
  const [inputVal, setInputVal] = useState(null);
  const [classType, setClassType] = useState(null);


  //packs input and predetermined choice(if any) into state held in parent component: displayArray
  const submitHandler = () => {
    // props.displayArray
    // props.setDisplayArray()
    console.log("gets to submit");
    let newField = { name: inputVal, class: classType };
    props.setDisplayArray((prevState) => {
      let newState = [...props.displayArray, newField];
      return newState;
    });
  };

  const inputChangeHandler = (event) => {
    setInputVal(event.target.value);
  };

  const checkBoxHandler = (event) => {
    let temp = event.target.name;
    switch (temp) {
      case "artificer":
        setClassType("artificer");
        console.log(classType);
        break;
      case "not":
        console.log("doesnt make it");
        break;
      default:
        return;
    }
  };

  return (
    <div className="input-block-container">
      <form
        onSubmit={(event) => {
          event.preventDefault();
          submitHandler();
        }}
      >
        {/* requires name */}
        <div className="form-top">
          <label>Name: </label>
          <input
            type="text"
            onChange={(event) => {
              inputChangeHandler(event);
            }}
            value={inputVal}
            required
          ></input>
          <button type="submit">Generate</button>
        </div>
        {/* can choose predetermined class */}
        <div className="class-container">
          {/* holds: Artificer, Barbarian, Bard, Cleric */}
          <ul className="checkbox-grouping">
            <RadioPair
              name={checkboxArray[0].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[1].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[2].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[3].name}
              checkBoxHandler={checkBoxHandler}
            />
          </ul>
          {/* holds: Druid, Fighter, Monk, Paladin, Ranger */}
          <ul className="checkbox-grouping">
            <RadioPair
              name={checkboxArray[4].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[5].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[6].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[7].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[8].name}
              checkBoxHandler={checkBoxHandler}
            />
          </ul>
          {/* Rogue, Sorcerer, Warlock, Wizard */}
          <ul className="checkbox-grouping">
            <RadioPair
              name={checkboxArray[9].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[10].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[11].name}
              checkBoxHandler={checkBoxHandler}
            />
            <RadioPair
              name={checkboxArray[12].name}
              checkBoxHandler={checkBoxHandler}
            />
          </ul>
        </div>
      </form>
    </div>
  );
}
